--[[
@description    Hosi Mini Track Mixer (ReaImGui Version)
@author         Hosi
@version        1.7
@reaper_version 6.0
@provides
  [main] . > Hosi_Mini Track Mixer (ReaImGui).lua

@about
  # Hosi Mini Track Mixer (ReaImGui Version)

  A small GUI workflow for mixing track volume, pan, width, sends, receives, and FX.

  ## Requirements:
  - REAPER 6.0+
  - ReaImGui library
  - SWS/S&M Extension (optional, for better track selection focus)

@changelog
  v1.7 (2025-10-17)
  - Added: Drag & Drop functionality to reorder items in the "Configure Menu" window.
  - Improved: Menu configuration window now uses icons for better visual distinction of item types.
  v1.6 (2025-10-16)
  - Added: A customizable user menu (‚öôÔ∏è icon) to run any REAPER action or script from the toolbar.
  - Added: Configuration window to add/remove custom menu items. Settings are saved globally.
  v1.5 (2025-10-15)
  - Added: A vertical stereo peak meter is now displayed next to the fader in the Channel Strip view for real-time visual feedback.
  - Added: Detailed tooltips for most UI elements to improve user guidance and clarity.
  - Added: Quick Comp controls in the Channel Strip view.
  v1.4 (2025-10-14)
  - Added: Channel Strip View
  - Added: A horizontal stereo VU meter is now displayed for each track, providing real-time visual feedback on audio levels.
  - Added: "Sync View" checkbox to synchronize the script's track filter with REAPER's main track view (TCP/Mixer).
  - Added: When Sync View is active, changing the view mode dropdown will automatically show/hide tracks in the project.
  - Added: Disabling Sync View will reset and show all tracks in the project.
  - Added: Display of the formatted parameter value (e.g., "-12.5 dB") next to each slider in FX PARAMS mode for precise control.
  - Improved: Replaced the static "VIEW" button in SENDS, RECEIVES, and FX PARAMS modes with the persistent View Mode dropdown filter.
  - Improved: The View Mode filter now applies to all editing modes for a more consistent workflow.
  - Added: FX Parameter Control mode. Users can now pin specific plugin parameters to the mixer for direct control.
  - Added: "Pin" button on each track to open a parameter selection window.
  - Added: Track Receives control mode, allowing users to view and manage all incoming signals for a selected track.
  - Added: Track Sends control mode. In this mode, the mixer displays tracks with sends, allowing detailed control over each send's volume, pan, and mute.
  v1.3 (2025-10-13)
  - Added: New View Modes: "SELECTED & CHILDREN", "ARMED", "HAS FX", "HAS ITEMS", "MUTED".
  - Added: Stereo Width control. The mode button now cycles through PAN, VOL, and WIDTH.
  - Added: Phase Invert button (√∏) for each track.
  - Improved: Replaced "Gang" checkbox with selection-based ganging.
  - Added: Track visibility control and toolbar buttons.
  v1.2 (2025-09-23)
  - Added: Right-click context menu.
  v1.1 (2025-09-23)
  - Added: Double-click to collapse/expand folders.
  v1.0 (2025-09-23)
  - Initial release.
--]]
local config={win_title="Hosi Mini Track Mixer v1.7",refresh_interval=0.05,indent_size=15.0} package.path=reaper.ImGui_GetBuiltinPath()..'/?.lua;'..package.path local imgui=require('imgui')('0.10') if not imgui or type(imgui)~="table" then reaper.ShowMessageBox("Could not initialize ReaImGui library.\n\nPlease install it (v0.10+) via ReaPack.","ReaImGui Error",0) return end local ctx=imgui.CreateContext(config.win_title) local has_sws_set_last_touched=type(reaper.SetLastTouchedTrack)=='function' local new_view_modes={"ALL","FOLDERS","FOLDERS & CHILDREN","SELECTED","SELECTED & CHILDREN","ARMED","HAS FX","HAS ITEMS","MUTED"} local state={is_open=true,edit_mode="PAN",view_mode=1,view_mode_names=new_view_modes,view_mode_names_str=table.concat(new_view_modes,"\0").."\0",filter_text="",sync_view=false,tracks={},tracksPan={},tracksVol={},tracksWidth={},tracksSel={},tracksMut={},tracksSol={},tracksFX={},tracksFXCount={},tracksArmed={},tracksHasItems={},tracksDepth={},isFolder={},folderCollapsed={},tracksVisible={},tracksPhase={},tracksSends={},tracksReceives={},tracksPeakL={},tracksPeakR={},tracksPeakHoldL={},tracksPeakHoldR={},last_selected_track_idx=nil,sends_view_track_idx=nil,receives_view_track_idx=nil,pinnedParams={},popup_pin_track_idx=nil,open_pin_popup=false,last_update_time=0,quickControls={reaComp_idx=nil,reaComp_params={}},preset1={track={}},preset2={track={}},preset3={track={}},preset4={track={}},preset5={track={}},customMenu={},open_custom_menu_config=false,new_menu_item_name="",new_menu_item_id="",new_menu_item_type_idx=0,menu_config_target_table=nil,dnd_source_path=nil,dnd_target_path=nil,dnd_drop_type=nil,item_to_delete_path=nil} function PackColor(r,g,b,a) local r_int=math.floor(r*255+0.5) local g_int=math.floor(g*255+0.5) local b_int=math.floor(b*255+0.5) local a_int=math.floor(a*255+0.5) return a_int*0x1000000+b_int*0x10000+g_int*0x100+r_int end function GainToDB(gain) if gain<0.0000000298 then return -144.0 end return 20*(math.log(gain)/math.log(10)) end function DBToGain(db) return 10^(db/20) end function ShowTooltip(text) if imgui.IsItemHovered(ctx) then imgui.BeginTooltip(ctx) imgui.Text(ctx,text) imgui.EndTooltip(ctx) end end function ForceUIRefresh() reaper.TrackList_AdjustWindows(false) reaper.UpdateArrange() end function DrawVUMeter(ctx,height,peakL_gain,peakR_gain,peakHoldL_gain,peakHoldR_gain,track_idx) local draw_list=imgui.GetWindowDrawList(ctx) local screen_px,screen_py=imgui.GetCursorScreenPos(ctx) local meter_w,meter_spacing=25,4 local total_w=meter_w*2+meter_spacing local text_height=20 imgui.InvisibleButton(ctx,"##vu_placeholder_area",total_w,height+text_height) if imgui.IsItemClicked(ctx,0) then state.tracksPeakHoldL[track_idx]=0 state.tracksPeakHoldR[track_idx]=0 ShowTooltip("Peak Reset") else ShowTooltip("Click meter to reset peak hold") end local col_bg=PackColor(0.2,0.2,0.2,1) local col_green=PackColor(0.1,1.0,0.1,1) local col_yellow=PackColor(1.0,1.0,0.1,1) local col_red=PackColor(1.0,0.2,0.2,1) local col_peak_hold=PackColor(1.0,1.0,1.0,1) local function DrawBar(x_offset,peak_gain,peak_hold_gain) local db=GainToDB(peak_gain) local meter_range_db=60 local meter_headroom_db=6 local bar_h=0 if db>=-meter_range_db then bar_h=(db+meter_range_db)/(meter_range_db+meter_headroom_db)*height bar_h=math.min(bar_h,height) end imgui.DrawList_AddRectFilled(draw_list,screen_px+x_offset,screen_py,screen_px+x_offset+meter_w,screen_py+height,col_bg) if bar_h>0 then local yellow_thresh=height*(meter_range_db-9)/(meter_range_db+meter_headroom_db) local red_thresh=height*meter_range_db/(meter_range_db+meter_headroom_db) local green_h=math.min(bar_h,yellow_thresh) imgui.DrawList_AddRectFilled(draw_list,screen_px+x_offset,screen_py+height-green_h,screen_px+x_offset+meter_w,screen_py+height,col_green) if bar_h>yellow_thresh then local yellow_h=math.min(bar_h,red_thresh)-yellow_thresh imgui.DrawList_AddRectFilled(draw_list,screen_px+x_offset,screen_py+height-yellow_thresh-yellow_h,screen_px+x_offset+meter_w,screen_py+height-yellow_thresh,col_yellow) end if bar_h>red_thresh then local red_h=bar_h-red_thresh imgui.DrawList_AddRectFilled(draw_list,screen_px+x_offset,screen_py+height-red_thresh-red_h,screen_px+x_offset+meter_w,screen_py+height-red_thresh,col_red) end end local peak_hold_db=GainToDB(peak_hold_gain) if peak_hold_db>-meter_range_db then local peak_y_pos_h=(peak_hold_db+meter_range_db)/(meter_range_db+meter_headroom_db)*height local peak_y_pos=screen_py+height-math.min(peak_y_pos_h,height) imgui.DrawList_AddLine(draw_list,screen_px+x_offset,peak_y_pos,screen_px+x_offset+meter_w,peak_y_pos,col_peak_hold,1.5) end end DrawBar(0,peakL_gain,peakHoldL_gain) DrawBar(meter_w+meter_spacing,peakR_gain,peakHoldR_gain) local peak_hold_db_l=GainToDB(peakHoldL_gain) local peak_hold_db_r=GainToDB(peakHoldR_gain) local text_l=(peak_hold_db_l<-99) and "-inf" or string.format("%.1f",peak_hold_db_l) local text_r=(peak_hold_db_r<-99) and "-inf" or string.format("%.1f",peak_hold_db_r) local text_w_l=imgui.CalcTextSize(ctx,text_l) local text_w_r=imgui.CalcTextSize(ctx,text_r) local text_y_pos=screen_py+height+3 imgui.DrawList_AddText(draw_list,screen_px+(meter_w-text_w_l)/2,text_y_pos,PackColor(0.8,0.8,0.8,1),text_l) imgui.DrawList_AddText(draw_list,screen_px+meter_w+meter_spacing+(meter_w-text_w_r)/2,text_y_pos,PackColor(0.8,0.8,0.8,1),text_r) end function save_pinned_params() local data_parts={} for guid,pins in pairs(state.pinnedParams) do if pins and #pins>0 then local pin_parts={} for _,pin in ipairs(pins) do table.insert(pin_parts,pin.fx_idx..","..pin.param_idx) end table.insert(data_parts,guid..":"..table.concat(pin_parts,";")) end end local data_string=table.concat(data_parts,"|") reaper.SetProjExtState(0,"Hosi.MiniTrackMixer","Pins",data_string) end function load_pinned_params() local _,data_string=reaper.GetProjExtState(0,"Hosi.MiniTrackMixer","Pins") state.pinnedParams={} if data_string and data_string~="" then for track_data in string.gmatch(data_string,"([^|]+)") do local guid,pins_str=string.match(track_data,"([^:]+):([^:]+)") if guid and pins_str then state.pinnedParams[guid]={} for pin_data in string.gmatch(pins_str,"([^;]+)") do local fx_idx,param_idx=string.match(pin_data,"([^,]+),([^,]+)") if fx_idx and param_idx then table.insert(state.pinnedParams[guid],{fx_idx=tonumber(fx_idx),param_idx=tonumber(param_idx)}) end end end end end end function serialize_table(val) if type(val)=="string" then return string.format("%q",val) elseif type(val)=="number" or type(val)=="boolean" then return tostring(val) elseif type(val)=="table" then local parts={} local is_array=true local n=0 for k,_ in pairs(val) do n=n+1 if type(k)~="number" or k~=n then is_array=false end end if n~=#val then is_array=false end for k,v in ipairs(val) do table.insert(parts,serialize_table(v)) end if not is_array then for k,v in pairs(val) do if type(k)=="number" and k>0 and k<=#val and val[k]==v then else table.insert(parts,string.format("[%s]=%s",serialize_table(k),serialize_table(v))) end end end return"{"..table.concat(parts,",").."}" else return"nil" end end function save_custom_menu() local data_string=serialize_table(state.customMenu) reaper.SetExtState("Hosi.MiniTrackMixer","CustomMenu",data_string,true) end function load_custom_menu() local data_string=reaper.GetExtState("Hosi.MiniTrackMixer","CustomMenu") if data_string and data_string~="" then local success,result=pcall(load("return "..data_string)) if success and type(result)=="table" then state.customMenu=result else state.customMenu={} end else state.customMenu={} end end function ApplyViewFilter(i) local track=state.tracks[i] local show_track=true local current_view_mode=state.view_mode_names[state.view_mode] if current_view_mode=="FOLDERS" then if not state.isFolder[i] then show_track=false end elseif current_view_mode=="FOLDERS & CHILDREN" then if not state.isFolder[i] and state.tracksDepth[i]==0 then show_track=false end elseif current_view_mode=="SELECTED" then if not state.tracksSel[i] then show_track=false end elseif current_view_mode=="SELECTED & CHILDREN" then local is_visible=state.tracksSel[i] if not is_visible then local parent=reaper.GetParentTrack(track) while parent do if reaper.IsTrackSelected(parent) then is_visible=true break end parent=reaper.GetParentTrack(parent) end end if not is_visible then show_track=false end elseif current_view_mode=="ARMED" then if not state.tracksArmed[i] then show_track=false end elseif current_view_mode=="HAS FX" then if state.tracksFXCount[i]<1 then show_track=false end elseif current_view_mode=="HAS ITEMS" then if not state.tracksHasItems[i] then show_track=false end elseif current_view_mode=="MUTED" then if state.tracksMut[i]~=1 then show_track=false end end return show_track end function SyncTrackVisibility() reaper.Undo_BeginBlock() reaper.PreventUIRefresh(1) for i=1,#state.tracks do local guid_str=reaper.GetTrackGUID(state.tracks[i]) local should_show=ApplyViewFilter(i) if state.edit_mode=="SENDS" and #state.tracksSends[i]==0 then should_show=false elseif state.edit_mode=="RECEIVES" and #state.tracksReceives[i]==0 then should_show=false elseif state.edit_mode=="FX PARAMS" and(not state.pinnedParams[guid_str] or #state.pinnedParams[guid_str]==0) then should_show=false end local new_vis_state=should_show and 1 or 0 reaper.SetMediaTrackInfo_Value(state.tracks[i],"B_SHOWINTCP",new_vis_state) reaper.SetMediaTrackInfo_Value(state.tracks[i],"B_SHOWINMIXER",new_vis_state) end reaper.PreventUIRefresh(-1) reaper.Undo_EndBlock("Sync Track Visibility",-1) ForceUIRefresh() end function ResetAllTracksVisibility() reaper.Undo_BeginBlock() reaper.PreventUIRefresh(1) for i=1,#state.tracks do reaper.SetMediaTrackInfo_Value(state.tracks[i],"B_SHOWINTCP",1) reaper.SetMediaTrackInfo_Value(state.tracks[i],"B_SHOWINMIXER",1) end reaper.PreventUIRefresh(-1) reaper.Undo_EndBlock("Show All Tracks",-1) ForceUIRefresh() end function update_quick_controls(track) state.quickControls={reaComp_idx=nil,reaComp_params={}} if not track then return end local fx_count=reaper.TrackFX_GetCount(track) for i=0,fx_count-1 do local _,fx_name=reaper.TrackFX_GetFXName(track,i,"") if fx_name then if not state.quickControls.reaComp_idx and fx_name:find("ReaComp",1,true) then state.quickControls.reaComp_idx=i state.quickControls.reaComp_params.bypass=reaper.TrackFX_GetEnabled(track,i) break end end end end function update_and_check_tracks() local new_track_count=reaper.CountTracks(0) local last_track_count=#state.tracks if last_track_count~=new_track_count then state.tracks,state.tracksPan,state.tracksVol,state.tracksWidth,state.tracksSel,state.tracksDepth,state.isFolder,state.folderCollapsed,state.tracksVisible,state.tracksPhase,state.tracksArmed,state.tracksHasItems,state.tracksFXCount,state.tracksSends,state.tracksReceives,state.tracksPeakL,state.tracksPeakR,state.tracksPeakHoldL,state.tracksPeakHoldR={},{},{},{},{},{},{},{},{},{},{},{},{},{},{},{},{},{},{} end state.last_selected_track_idx=nil local last_sel_track=reaper.GetLastTouchedTrack() for i=1,new_track_count do local track=reaper.GetTrack(0,i-1) state.tracks[i]=track state.tracksPan[i]=reaper.GetMediaTrackInfo_Value(track,"D_PAN") state.tracksVol[i]=reaper.GetMediaTrackInfo_Value(track,"D_VOL") state.tracksWidth[i]=reaper.GetMediaTrackInfo_Value(track,"D_WIDTH") state.tracksMut[i]=reaper.GetMediaTrackInfo_Value(track,"B_MUTE") state.tracksSol[i]=reaper.GetMediaTrackInfo_Value(track,"I_SOLO") state.tracksFX[i]=reaper.TrackFX_GetChainVisible(track) state.tracksDepth[i]=reaper.GetTrackDepth(track) state.isFolder[i]=reaper.GetMediaTrackInfo_Value(track,"I_FOLDERDEPTH")==1 state.tracksVisible[i]=reaper.GetMediaTrackInfo_Value(track,"B_SHOWINTCP")==1 state.tracksSel[i]=reaper.IsTrackSelected(track) state.tracksPhase[i]=reaper.GetMediaTrackInfo_Value(track,"B_PHASE") state.tracksArmed[i]=reaper.GetMediaTrackInfo_Value(track,"I_RECARM")==1 state.tracksHasItems[i]=reaper.CountTrackMediaItems(track)>0 state.tracksFXCount[i]=reaper.TrackFX_GetCount(track) local currentPeakL_gain=reaper.Track_GetPeakInfo(track,0) local currentPeakR_gain=reaper.Track_GetPeakInfo(track,1) state.tracksPeakL[i]=currentPeakL_gain state.tracksPeakR[i]=currentPeakR_gain state.tracksPeakHoldL[i]=state.tracksPeakHoldL[i] or 0 state.tracksPeakHoldR[i]=state.tracksPeakHoldR[i] or 0 if currentPeakL_gain>state.tracksPeakHoldL[i] then state.tracksPeakHoldL[i]=currentPeakL_gain end if currentPeakR_gain>state.tracksPeakHoldR[i] then state.tracksPeakHoldR[i]=currentPeakR_gain end state.tracksSends[i]={} state.tracksReceives[i]={} if state.folderCollapsed[i]==nil then state.folderCollapsed[i]=false end if last_sel_track and track==last_sel_track then state.last_selected_track_idx=i end end if state.last_selected_track_idx then update_quick_controls(state.tracks[state.last_selected_track_idx]) else update_quick_controls(nil) end for i=1,new_track_count do local src_track=state.tracks[i] local _,src_name=reaper.GetTrackName(src_track) local send_count=reaper.GetTrackNumSends(src_track,0) for j=0,send_count-1 do local dest_track_ptr=reaper.GetTrackSendInfo_Value(src_track,0,j,"P_DESTTRACK") if dest_track_ptr and reaper.ValidatePtr(dest_track_ptr,"MediaTrack*") then local send_vol=reaper.GetTrackSendInfo_Value(src_track,0,j,"D_VOL") local send_pan=reaper.GetTrackSendInfo_Value(src_track,0,j,"D_PAN") local send_mute=reaper.GetTrackSendInfo_Value(src_track,0,j,"B_MUTE") local _,dest_name=reaper.GetTrackName(dest_track_ptr) table.insert(state.tracksSends[i],{vol=send_vol,pan=send_pan,mute=send_mute,dest_track_name=dest_name}) for k=1,new_track_count do if state.tracks[k]==dest_track_ptr then table.insert(state.tracksReceives[k],{src_track=src_track,src_track_name=src_name,send_idx=j,vol=send_vol,pan=send_pan,mute=send_mute}) break end end end end end end function save_preset(preset_num) local preset_table=state["preset"..preset_num] if not preset_table then return end preset_table.track,preset_table.pan,preset_table.vol,preset_table.mute,preset_table.solo={},{},{},{},{} for i=1,#state.tracks do preset_table.track[i]=state.tracks[i] preset_table.pan[i]=state.tracksPan[i] preset_table.vol[i]=state.tracksVol[i] preset_table.mute[i]=state.tracksMut[i] preset_table.solo[i]=state.tracksSol[i] end reaper.ShowMessageBox("Preset "..preset_num.." saved.","Notification",0) end function load_preset(preset_num) local preset_table=state["preset"..preset_num] if not preset_table or #preset_table.track==0 then reaper.ShowMessageBox("Preset "..preset_num.." is empty.","Notification",0) return end for i=1,#preset_table.track do local track=state.tracks[i] if track and reaper.ValidatePtr(track,"MediaTrack*") then reaper.SetMediaTrackInfo_Value(track,"D_VOL",preset_table.vol[i]) reaper.SetMediaTrackInfo_Value(track,"D_PAN",preset_table.pan[i]) reaper.SetMediaTrackInfo_Value(track,"B_MUTE",preset_table.mute[i]) reaper.SetMediaTrackInfo_Value(track,"I_SOLO",preset_table.solo[i]) end end reaper.ShowMessageBox("Preset "..preset_num.." loaded.","Notification",0) end function CycleEditMode() if state.edit_mode=="PAN" then state.edit_mode="VOL" elseif state.edit_mode=="VOL" then state.edit_mode="WIDTH" elseif state.edit_mode=="WIDTH" then state.edit_mode="SENDS" elseif state.edit_mode=="SENDS" then state.edit_mode="RECEIVES" elseif state.edit_mode=="RECEIVES" then state.edit_mode="FX PARAMS" elseif state.edit_mode=="FX PARAMS" then state.edit_mode="CHAN STRIP" else state.edit_mode="PAN" end if state.sync_view then SyncTrackVisibility() end end function DrawRecursiveMenu(menu_table) for _,item in ipairs(menu_table) do if item.type=="item" then if imgui.MenuItem(ctx,item.name) then local num_cmd=tonumber(item.commandId) if num_cmd then reaper.Main_OnCommand(num_cmd,0) else reaper.Main_OnCommand(reaper.NamedCommandLookup(item.commandId),0) end end elseif item.type=="menu" then if imgui.BeginMenu(ctx,item.name) then DrawRecursiveMenu(item.items) imgui.EndMenu(ctx) end elseif item.type=="separator" then imgui.Separator(ctx) end end end function get_parent_table_and_index(path_str) local path={} for part in string.gmatch(path_str,"[^%.]+") do table.insert(path,tonumber(part)) end if #path==0 then return nil,nil end local parent={items=state.customMenu} local current_table=state.customMenu for i=1,#path-1 do local idx=path[i] local item=current_table[idx] if item and item.type=='menu' and item.items then parent=item current_table=item.items else return nil,nil end end return parent.items,path[#path] end function execute_menu_move(source_path,target_path,drop_type) local source_parent,source_idx=get_parent_table_and_index(source_path) if not source_parent then return end local item_to_move=table.remove(source_parent,source_idx) if not item_to_move then return end if drop_type=='into' then local target_parent_table,target_idx=get_parent_table_and_index(target_path) if string.find(target_path,source_path..".",1,true) then table.insert(source_parent,source_idx,item_to_move) return end if target_parent_table and target_parent_table[target_idx] and target_parent_table[target_idx].type=='menu' then local target_item=target_parent_table[target_idx] table.insert(target_item.items,item_to_move) else table.insert(source_parent,source_idx,item_to_move) end else local target_parent,target_idx=get_parent_table_and_index(target_path) if not target_parent then table.insert(source_parent,source_idx,item_to_move) return end if source_parent==target_parent and source_idx<target_idx then target_idx=target_idx-1 end table.insert(target_parent,target_idx,item_to_move) end save_custom_menu() end function execute_menu_delete(path_to_delete) local parent_table,idx=get_parent_table_and_index(path_to_delete) if parent_table and idx then table.remove(parent_table,idx) save_custom_menu() end end function DrawMenuConfiguration(menu_table,path_prefix) for i,item in ipairs(menu_table) do local current_path=path_prefix..i local id_str=tostring(item) imgui.InvisibleButton(ctx,"drop_target_before##"..current_path,-1,4) if imgui.BeginDragDropTarget(ctx) then local success,payload_str=imgui.AcceptDragDropPayload(ctx,"CUSTOM_MENU_ITEM") if success and payload_str~=current_path then state.dnd_source_path=payload_str state.dnd_target_path=current_path state.dnd_drop_type='before' end imgui.EndDragDropTarget(ctx) end local node_open=false local item_label if item.type=="menu" then item_label="üìÅ "..item.name.." (Sub-menu)" elseif item.type=="item" then item_label="üìÑ "..item.name.." (Action)" elseif item.type=="separator" then item_label="--- Separator ---" end if item.type=="menu" then node_open=imgui.TreeNode(ctx,item_label.."##"..id_str) else imgui.Selectable(ctx,item_label.."##"..id_str,false) end if imgui.BeginDragDropSource(ctx) then imgui.SetDragDropPayload(ctx,"CUSTOM_MENU_ITEM",current_path) imgui.Text(ctx,"Moving: "..item.name) imgui.EndDragDropSource(ctx) end if item.type=='menu' then if imgui.BeginDragDropTarget(ctx) then local success,payload_str=imgui.AcceptDragDropPayload(ctx,"CUSTOM_MENU_ITEM") if success and payload_str~=current_path then state.dnd_source_path=payload_str state.dnd_target_path=current_path state.dnd_drop_type='into' end imgui.EndDragDropTarget(ctx) end end if imgui.IsItemClicked(ctx,0) and item.type=="menu" then state.menu_config_target_table=item.items end imgui.SameLine(ctx) if imgui.SmallButton(ctx,"Delete##"..id_str) then state.item_to_delete_path=current_path end if node_open then DrawMenuConfiguration(item.items,current_path..".") imgui.TreePop(ctx) end end imgui.InvisibleButton(ctx,"drop_target_end##"..path_prefix,-1,4) if imgui.BeginDragDropTarget(ctx) then local success,payload_str=imgui.AcceptDragDropPayload(ctx,"CUSTOM_MENU_ITEM") if success then local target_path=path_prefix..(#menu_table+1) state.dnd_source_path=payload_str state.dnd_target_path=target_path state.dnd_drop_type='before' end imgui.EndDragDropTarget(ctx) end end function loop() local current_time=reaper.time_precise() if current_time-state.last_update_time>config.refresh_interval then update_and_check_tracks() state